templates.template:

        /* Enter a unique ExecutionPlan */
        @Plan:name('Analytics-ExecutionPlan-AnomalousEventsDetection')

        /* Enter a unique description for ExecutionPlan */
        -- @Plan:description('ExecutionPlan')

        /* define streams/tables and write queries here ... */

        @Import('org.wso2.analytics.stream.$referenceTypeAnomalyDetection:1.0.0')
        define stream InputStream (ReferenceID string, ReferenceValue LONG, timeStamp long);



@Export('org.wso2.analytics.apim.emailAlertStream:1.0.0')
define stream emailAlert (type string, msg string, alertTimestamp string, emails string, severity string);

        partition with ( ReferenceID of InputStream )
        begin
            from InputStream#timeseries:outlier($calculationInterval, $batchSize, $confidenceInterval, $range, ReferenceValue, timeStamp)
            select ReferenceID, ReferenceValue, timeStamp, outlier as notify
            insert into tempStream;
        end;




        from tempStream[notify == true]#log('nuwan')
        select 'Anomaly Detected' as type, str:concat("$referenceType of id ", ReferenceID, " had a value of ", ReferenceValue) as msg, 'timeStamp' as alertTimestamp, $email as emails, 'high' as severity
        insert into emailAlert;


